import React, { useState } from 'react';
import MoodSelector from './components/MoodSelector';
import SongList from './components/SongList';
import Header from './components/Header';
import Loading from './components/Loading';
import axios from 'axios';
import './styles/index.css';

function App() {
  const [selectedMood, setSelectedMood] = useState('');
  const [songs, setSongs] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [playlistInfo, setPlaylistInfo] = useState(null);

  const handleMoodSelect = async (mood) => {
    setSelectedMood(mood);
    setLoading(true);
    setSongs([]);
    setError('');
    setPlaylistInfo(null);

    try {
      const response = await axios.post('/api/recommend', { mood, limit: 12 });
      setSongs(response.data.tracks);
      setPlaylistInfo({
        name: response.data.playlist_name,
        url: response.data.playlist_url
      });
      setLoading(false);
    } catch (err) {
      console.error('Error fetching songs:', err);
      setError('Failed to fetch songs. Please try again.');
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <Header />
      <MoodSelector onMoodSelect={handleMoodSelect} selectedMood={selectedMood} />
      
      {error && <div className="error-message">{error}</div>}
      
      {loading ? (
        <Loading />
      ) : (
        songs.length > 0 && (
          <SongList songs={songs} playlistInfo={playlistInfo} />
        )
      )}
    </div>
  );
}

export default App;
